ewCell *cell = [[[UITableViewCell alloc] 							  initWithStyle:UITableViewCellStyleDefault 							  reuseIdentifier:nameAtIndexPath] autorelease];		NSString *path = [[NSBundle mainBundle] pathForResource:@"cafe" ofType:@"png"];	UIImage *theImage = [UIImage imageWithContentsOfFile:path];		cell.textLabel.text = nameAtIndexPath;	cell.textLabel.backgroundColor = [UIColor clearColor];	cell.textLabel.textColor = [UIColor whiteColor];	cell.textLabel.shadowColor = [UIColor colorWithRed:0 green:0 blue:0 alpha:0.5];	cell.textLabel.shadowOffset = CGSizeMake(0, -1);	cell.imageView.image = theImage;	[cell setAccessoryType:UITableViewCellAccessoryDisclosureIndicator];		[cell setSelectionStyle:UITableViewCellSelectionStyleGray];		if ([indexPath row] == 0) {				UIImage *image = [[UIImage imageNamed:@"tableViewHead.png"] retain];			UIImageView *imageView = [[UIImageView alloc] initWithImage:image];		imageView.image = image;		imageView.opaque = YES;		cell.backgroundView = imageView;				[imageView release];			}else if ([indexPath row] == [[producte valueForKey:@"begudes"] count]-1) {		UIImage *image = [[UIImage imageNamed:@"tableViewFooter.png"] retain];			UIImageView *imageView = [[UIImageView alloc] initWithImage:image];		imageView.image = image;		imageView.opaque = YES;		cell.backgroundView = imageView;				[imageView release];	}else{		UIImage *image = [[UIImage imageNamed:@"tableViewMiddle.png"] retain];			UIImageView *imageView = [[UIImageView alloc] initWithImage:image];		imageView.image = image;		imageView.opaque = YES;		cell.backgroundView = imageView;				[imageView release];	}		return cell;}-(NSInteger) tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section {	return [[producte valueForKey:@"begudes"] count];	}-(NSString *) tableView:(UITableView *)tableView titleForHeaderInSection:(NSInteger)section{	return [producte valueForKey:@"nom"];}- (UIView *)tableView:(UITableView *)tableView viewForHeaderInSection:(NSInteger)section{	// create the parent view that will hold header Label	UIView* customView = [[UIView alloc] initWithFrame:CGRectMake(10.0, 0.0, 300.0, 44.0)];		// create the button object	UILabel * headerLabel = [[UILabel alloc] initWithFrame:CGRectZero];	headerLabel.backgroundColor = [UIColor clearColor];	headerLabel.opaque = NO;	headerLabel.textColor = [UIColor colorWithRed:0.28 green:0.41 blue:0.32 alpha:1];	headerLabel.highlightedTextColor = [UIColor whiteColor];	headerLabel.font = [UIFont boldSystemFontOfSize:17];	headerLabel.frame = CGRectMake(20.0, 0.0, 300.0, 44.0);		// If you want to align the header text as centered	// headerLabel.frame = CGRectMake(150.0, 0.0, 300.0, 44.0);		headerLabel.text = [producte valueForKey:@"nom"]; // i.e. array element		headerLabel.shadowColor = [UIColor colorWithRed:1 green:1 blue:1 alpha:0.5];	[customView addSubview:headerLabel];		return customView;}- (CGFloat) tableView:(UITableView *)tableViewActual heightForHeaderInSection:(NSInteger)section{	return 44.0;}-(void) tableView:(UITableView *)tableViewActual didSelectRowAtIndexPath:(NSIndexPath *)indexPath {    	appDelegate = [ExpressoAppDelegate instance];	[appDelegate setSubproducte:[NSNumber numberWithUnsignedInteger:[indexPath row]]];    	[tableViewActual deselectRowAtIndexPath:indexPath animated:YES];		//Push de la nova vista	TamanysViewController *tamanysVC = [[[TamanysViewController alloc] 									   initWithNibName:@"TamanysViewController" bundle:nil] autorelease];		[tamanysVC setSubproducte:[[producte valueForKey:@"begudes"] objectAtIndex:[indexPath row]]];		[appDelegate.navigationController pushViewController:tamanysVC animated:YES];}@end