Cell alloc] 							  initWithStyle:UITableViewCellStyleDefault 							  reuseIdentifier:nameAtIndexPath] autorelease];		NSString *path = [[NSBundle mainBundle] pathForResource:@"cafe" ofType:@"png"];	UIImage *theImage = [UIImage imageWithContentsOfFile:path];		cell.textLabel.text = nameAtIndexPath;	cell.imageView.image = theImage;	[cell setAccessoryType:UITableViewCellAccessoryDisclosureIndicator];		[cell setSelectionStyle:UITableViewCellSelectionStyleBlue];		return cell;}-(NSInteger) tableView:(UITableView *)tableView numberOfRowsInSection:(NSInteger)section {	return [[producte valueForKey:@"subproductes"] count];	}-(NSString *) tableView:(UITableView *)tableView titleForHeaderInSection:(NSInteger)section{	return [producte valueForKey:@"nom"];}- (UIView *)tableView:(UITableView *)tableView viewForHeaderInSection:(NSInteger)section{	// create the parent view that will hold header Label	UIView* customView = [[UIView alloc] initWithFrame:CGRectMake(10.0, 0.0, 300.0, 44.0)];		// create the button object	UILabel * headerLabel = [[UILabel alloc] initWithFrame:CGRectZero];	headerLabel.backgroundColor = [UIColor clearColor];	headerLabel.opaque = NO;	headerLabel.textColor = [UIColor colorWithRed:0.28 green:0.41 blue:0.32 alpha:1];	headerLabel.highlightedTextColor = [UIColor whiteColor];	headerLabel.font = [UIFont boldSystemFontOfSize:17];	headerLabel.frame = CGRectMake(20.0, 0.0, 300.0, 44.0);		// If you want to align the header text as centered	// headerLabel.frame = CGRectMake(150.0, 0.0, 300.0, 44.0);		headerLabel.text = [producte valueForKey:@"nom"]; // i.e. array element		headerLabel.shadowColor = [UIColor colorWithRed:1 green:1 blue:1 alpha:0.5];	[customView addSubview:headerLabel];		return customView;}- (CGFloat) tableView:(UITableView *)tableView heightForHeaderInSection:(NSInteger)section{	return 44.0;}-(void) tableView:(UITableView *)tableView didSelectRowAtIndexPath:(NSIndexPath *)indexPath {	ExpressoAppDelegate *appDelegate = [[UIApplication sharedApplication] delegate];	[appDelegate setProducte:[indexPath row]];			}@end