yle];		[f setCurrencySymbol:@"€ "];    }    return self;}// Implement viewDidLoad to do additional setup after loading the view, typically from a nib.- (void)viewDidLoad {	[super viewDidLoad];		appDelegate = (ExpressoAppDelegate*)[[UIApplication sharedApplication] delegate];	//Si venim de favorit	if ([[appDelegate nomProducte] length]>0){		[producte setText:[appDelegate nomProducte]];				//No podem guardar-lo si ja venim de favorits		[guardarBeguda setEnabled:NO];		[guardarBeguda setAlpha:0.5];	}	//Si venim de selecció normal	else {				NSLog(@"Producte: %@", [appDelegate producte]);				producteDictionary = [[[[appDelegate carta] valueForKey:@"carta"] valueForKey:@"productes"]							  objectAtIndex:[[appDelegate producte] unsignedIntValue]];				[producte setText:[producteDictionary valueForKey:@"nom"]];				[subproducte setText:[[[producteDictionary valueForKey:@"subproductes"] 							   objectAtIndex:[[appDelegate subproducte] unsignedIntValue]] valueForKey:@"nom"]];				[extres setText:[[[appDelegate extres] allKeys] componentsJoinedByString:@", "]];						NSNumberFormatter *suma = [[NSNumberFormatter alloc] init];		[suma setNumberStyle:NSNumberFormatterDecimalStyle];				float aux = [appDelegate.preu floatValue];		for (int i=0; i<[appDelegate.extres count]; i++) {			aux += [[suma numberFromString:[[appDelegate.extres allValues] objectAtIndex:i]] floatValue];			NSLog(@"%.f", aux);		}		[suma release];				[appDelegate setPreu:[NSNumber numberWithFloat:aux]];	}		[preu setText:[f stringFromNumber:[appDelegate preu]]];		}/*// Override to allow orientations other than the default portrait orientation.- (BOOL)shouldAutorotateToInterfaceOrientation:(UIInterfaceOrientation)interfaceOrientation {    // Return YES for supported orientations.    return (interfaceOrientation == UIInterfaceOrientationPortrait);}*/- (void)didReceiveMemoryWarning {    // Releases the view if it doesn't have a superview.    [super didReceiveMemoryWarning];        // Release any cached data, images, etc. that aren't in use.}- (void)viewDidUnload {    [super viewDidUnload];    // Release any retained subviews of the main view.    // e.g. self.myOutlet = nil;}- (void)dealloc {    [super dealloc];}@end